import { stringify } from "querystring";
import React, { useEffect } from "react";
import { useForm } from "react-hook-form";

type Arbitrage = {
  [key in "home-win" | "draw" | "away-win"]: {
    platform: string;
    odd: number;
    arb: number;
  };
};

const IndexPage = () => {
  const [arbs, setArbitrage] = React.useState<Arbitrage>({
    "home-win": {
      platform: "",
      odd: 0.0,
      arb: 0.0,
    },
    draw: {
      platform: "",
      odd: 0.0,
      arb: 0.0,
    },
    "away-win": {
      platform: "",
      odd: 0.0,
      arb: 0.0,
    },
  });
  const [totalArbPercentile, setTotalArbPercentile] = React.useState(0);
  const [profit, setProfit] = React.useState<number>(0);
  const [inv, setInv] = React.useState<number>(0);

  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();

  function calcIndividualArb() {}

  function calcProfit() {}

  function calculateEverything() {}

  console.log(watch());

  useEffect(() => {
    const arb = {};
    Object.entries(watch()).forEach(([key, value]) => {
      if (key === "home-win" || key === "away-win" || key === "draw") {
        Object.assign(arb, {
          [key]: {
            platform: "",
            odd: parseFloat(value),
            arb: parseFloat((1 / parseFloat(value)) * 100),
          },
        });
      }

      if (key === "investment") {
        setInv(value);
      }
    });

    // @ts-ignore
    setArbitrage((prev: Arbitrage) => ({
      ...prev,
      ...arb,
    }));
  }, [JSON.stringify(watch())]);

  useEffect(() => {
    let totalPerc = 0;
    Object.entries(arbs).forEach(
      // @ts-ignore
      ([_, value]: [
        "home-win" | "draw" | "away-win",
        {
          platform: string;
          odd: number;
        }
      ]) => {
        if (value.odd) {
          totalPerc += (1 / value.odd) * 100;
        }
      }
    );

    setTotalArbPercentile(totalPerc);
  }, [JSON.stringify(arbs)]);

  useEffect(() => {
    if (totalArbPercentile) {
      const prof = inv / totalArbPercentile - inv;

      setProfit(prof);
    }
  }, [totalArbPercentile]);

  return (
    <div className="max-w-screen w-full place-items-center justify-center flex flex-col">
      <h1 className="text-2xl font-bold mb-[8px]">Calc Football Arb</h1>
      <div className="my-[8px]">
        <h6>Investment</h6>
        <input
          type="number"
          defaultValue={0.0}
          min={0.0}
          step={0.01}
          {...register("investment")}
        />
      </div>
      <div className="my-[8px]">
        <h6>Home Win</h6>
        <input
          type="number"
          defaultValue={0.0}
          min={0.0}
          step={0.01}
          {...register("home-win")}
        />
      </div>
      <div className="my-[8px]">
        <h6>Draw</h6>
        <input
          type="number"
          defaultValue={0.0}
          min={0.0}
          step={0.01}
          {...register("draw")}
        />
      </div>
      <div className="my">
        <h6>Away Win</h6>
        <input
          type="number"
          defaultValue={0.0}
          min={0.0}
          step={0.01}
          {...register("away-win")}
        />
      </div>
      <div className="mt-[8px] flex flex-col">
        <span>Investment: {inv}</span>
        <span>Percentile: {totalArbPercentile.toFixed(2)}</span>
        <span>Profit: {profit}</span>
      </div>
    </div>
  );
};

export default IndexPage;
